    private void brSalvarCCActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_brSalvarCCActionPerformed
        Principal.conta1.setNome(campoNome1.getText());
        Principal.conta1.setNumero(Integer.parseInt(campoNumero1.getText()));
        Principal.conta1.depositar(Double.parseDouble(campoSaldo1.getText()));
        
        new Aba2().setVisible(true);
        this.setVisible(false);
    }//GEN-LAST:event_brSalvarCCActionPerformed

